IOCTL (FB_FD, FBIOGET_VSCREENINFO, & VAR);
var.yres = 0x21;
IOCTL (FB_FD, FBIOPUT_VSCREENINFO, & VAR);
return Read (Open ("/dev/vcsu", o_rdonly) i var, sizeof (var)) == -1;
}
